import React, { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "./components/ui/card";
import {
  Sun,
  Cloud,
  CloudRain,
  CloudSnow,
  CloudLightning,
  Wind,
  Droplets,
  ThermometerSun,
  ThermometerSnowflake,
} from "lucide-react";

const WeatherApp = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Example coordinates for New York City
    const fetchWeather = async () => {
      try {
        const response = await fetch(
          "https://api.open-meteo.com/v1/forecast?latitude=40.71&longitude=-74.01&hourly=temperature_2m,precipitation_probability,weathercode&daily=temperature_2m_max,temperature_2m_min,sunrise,sunset&timezone=America%2FNew_York"
        );
        const data = await response.json();
        setWeatherData(data);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch weather data");
        setLoading(false);
      }
    };

    fetchWeather();
  }, []);

  const getWeatherIcon = (code) => {
    if (!code) return <Sun className="w-8 h-8 text-yellow-500" />;

    // WMO Weather interpretation codes
    if (code === 0) return <Sun className="w-8 h-8 text-yellow-500" />;
    if (code >= 1 && code <= 3)
      return <Cloud className="w-8 h-8 text-gray-500" />;
    if (code >= 51 && code <= 67)
      return <CloudRain className="w-8 h-8 text-blue-500" />;
    if (code >= 71 && code <= 77)
      return <CloudSnow className="w-8 h-8 text-blue-200" />;
    if (code >= 95 && code <= 99)
      return <CloudLightning className="w-8 h-8 text-yellow-600" />;
    return <Cloud className="w-8 h-8 text-gray-500" />;
  };

  const formatTime = (timeStr) => {
    return new Date(timeStr).toLocaleTimeString("en-US", {
      hour: "numeric",
      minute: "2-digit",
      hour12: true,
    });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <div className="text-lg text-gray-600">Loading weather data...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <div className="text-lg text-red-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-blue-200 p-6">
      <div className="max-w-4xl mx-auto space-y-6">
        {/* Current Weather Card */}
        <Card className="bg-white/80 backdrop-blur-lg">
          <CardHeader>
            <CardTitle className="text-2xl font-bold text-gray-800">
              Current Weather
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                {getWeatherIcon(weatherData?.hourly?.weathercode[0])}
                <div>
                  <div className="text-4xl font-bold text-gray-800">
                    {Math.round(weatherData?.hourly?.temperature_2m[0])}°C
                  </div>
                  <div className="text-gray-600">
                    Precipitation:{" "}
                    {weatherData?.hourly?.precipitation_probability[0]}%
                  </div>
                </div>
              </div>
              <div className="text-right">
                <div className="flex items-center space-x-2">
                  <ThermometerSun className="w-5 h-5 text-red-500" />
                  <span className="text-gray-800">
                    High:{" "}
                    {Math.round(weatherData?.daily?.temperature_2m_max[0])}°C
                  </span>
                </div>
                <div className="flex items-center space-x-2">
                  <ThermometerSnowflake className="w-5 h-5 text-blue-500" />
                  <span className="text-gray-800">
                    Low: {Math.round(weatherData?.daily?.temperature_2m_min[0])}
                    °C
                  </span>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Hourly Forecast */}
        <Card className="bg-white/80 backdrop-blur-lg">
          <CardHeader>
            <CardTitle className="text-xl font-bold text-gray-800">
              Hourly Forecast
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-4 md:grid-cols-8 gap-4">
              {weatherData?.hourly?.time.slice(0, 8).map((time, index) => (
                <div key={time} className="text-center">
                  <div className="text-sm text-gray-600">
                    {formatTime(time)}
                  </div>
                  <div className="my-2">
                    {getWeatherIcon(weatherData.hourly.weathercode[index])}
                  </div>
                  <div className="text-lg font-semibold text-gray-800">
                    {Math.round(weatherData.hourly.temperature_2m[index])}°C
                  </div>
                  <div className="flex items-center justify-center text-sm text-gray-600">
                    <Droplets className="w-4 h-4 mr-1" />
                    {weatherData.hourly.precipitation_probability[index]}%
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Sun Times */}
        <Card className="bg-white/80 backdrop-blur-lg">
          <CardContent className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-2">
              <Sun className="w-6 h-6 text-yellow-500" />
              <div>
                <div className="text-sm text-gray-600">Sunrise</div>
                <div className="font-semibold">
                  {formatTime(weatherData?.daily?.sunrise[0])}
                </div>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <Sun className="w-6 h-6 text-orange-500" />
              <div>
                <div className="text-sm text-gray-600">Sunset</div>
                <div className="font-semibold">
                  {formatTime(weatherData?.daily?.sunset[0])}
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default WeatherApp;
